import React, { useState } from 'react';
import { ChevronLeft, ChevronRight, Check, PlusCircle, Trash2 } from 'lucide-react';

const initialState = {
  policyNo: '',
  policyStatus: '',
  policyType: '',
  policyEffectiveDate: '',
  policyExpirationDate: '',
  policyHolder: {
    firstName: '',
    lastName: '',
    address: {
      street: '',
      city: '',
      state: '',
      zip: ''
    }
  },
  drivers: [{
    firstName: '',
    lastName: '',
    age: '',
    gender: '',
    maritalStatus: '',
    licenseNumber: '',
    licenseState: '',
    licenseStatus: '',
    licenseEffectiveDate: '',
    licenseExpirationDate: '',
    licenseClass: ''
  }],
  vehicles: [{
    year: '',
    make: '',
    model: '',
    vin: '',
    usage: '',
    primaryUse: '',
    annualMileage: '',
    ownership: '',
    garagingAddress: {
      street: '',
      city: '',
      state: '',
      zip: ''
    },
    coverages: [
      { type: 'Liability', limit: '', deductible: '' }
    ]
  }]
};

const steps = [
  { id: 1, title: 'Policy Info', description: 'Basic policy information' },
  { id: 2, title: 'Policy Holder', description: 'Personal information' },
  { id: 3, title: 'Driver Details', description: 'License information' },
  { id: 4, title: 'Vehicle & Coverage', description: 'Vehicle and coverage details' }
];

export default function PolicyForm({ isUpdate = false }) {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState(initialState);

  // Add/Remove Drivers
  const addDriver = () => {
    setFormData(prev => ({ ...prev, drivers: [...prev.drivers, {
      firstName: '', lastName: '', age: '', gender: '', maritalStatus: '', licenseNumber: '', licenseState: '', licenseStatus: '', licenseEffectiveDate: '', licenseExpirationDate: '', licenseClass: ''
    }] }));
  };
  const removeDriver = (idx: number) => {
    setFormData(prev => ({ ...prev, drivers: prev.drivers.filter((_, i) => i !== idx) }));
  };

  // Add/Remove Vehicles
  const addVehicle = () => {
    setFormData(prev => ({ ...prev, vehicles: [...prev.vehicles, {
      year: '', make: '', model: '', vin: '', usage: '', primaryUse: '', annualMileage: '', ownership: '', garagingAddress: { street: '', city: '', state: '', zip: '' }, coverages: [{ type: 'Liability', limit: '', deductible: '' }]
    }] }));
  };
  const removeVehicle = (idx: number) => {
    setFormData(prev => ({ ...prev, vehicles: prev.vehicles.filter((_, i) => i !== idx) }));
  };

  // Add/Remove Coverages for a vehicle
  const addCoverage = (vehicleIdx: number) => {
    setFormData(prev => {
      const vehicles = [...prev.vehicles];
      vehicles[vehicleIdx].coverages.push({ type: '', limit: '', deductible: '' });
      return { ...prev, vehicles };
    });
  };
  const removeCoverage = (vehicleIdx: number, coverageIdx: number) => {
    setFormData(prev => {
      const vehicles = [...prev.vehicles];
      vehicles[vehicleIdx].coverages = vehicles[vehicleIdx].coverages.filter((_, i) => i !== coverageIdx);
      return { ...prev, vehicles };
    });
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>, path: string) => {
    const value = e.target.value;
    setFormData(prev => {
      const updated = { ...prev };
      const keys = path.split('.');
      let ref = updated;
      while (keys.length > 1) {
        ref = (ref as any)[keys.shift() as string];
      }
      (ref as any)[keys[0]] = value;
      return updated;
    });
  };

  const handleSubmit = () => {
    console.log(formData);
    // TODO: Send formData to backend API
  };

  const nextStep = () => {
    if (currentStep < steps.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const isStepValid = () => {
    switch (currentStep) {
      case 1:
        return formData.policyNo && formData.policyStatus && formData.policyType && formData.policyEffectiveDate && formData.policyExpirationDate;
      case 2:
        return formData.policyHolder.firstName && formData.policyHolder.lastName &&
               formData.policyHolder.address.street && formData.policyHolder.address.city &&
               formData.policyHolder.address.state && formData.policyHolder.address.zip;
      case 3:
        return formData.drivers.every(driver =>
          driver.firstName && driver.lastName && driver.age && driver.gender &&
          driver.maritalStatus && driver.licenseNumber && driver.licenseState &&
          driver.licenseStatus && driver.licenseEffectiveDate && driver.licenseExpirationDate &&
          driver.licenseClass
        );
      case 4:
        return formData.vehicles.every(vehicle =>
          vehicle.year && vehicle.make && vehicle.model && vehicle.vin &&
          vehicle.usage && vehicle.primaryUse && vehicle.annualMileage &&
          vehicle.ownership && vehicle.garagingAddress.street &&
          vehicle.garagingAddress.city && vehicle.garagingAddress.state &&
          vehicle.garagingAddress.zip &&
          vehicle.coverages.every(c => c.type && c.limit && c.deductible)
        );
      default:
        return false;
    }
  };

  const StepHeader = () => (
    <div className="flex items-center justify-center mb-8">
      <div className="flex items-center space-x-4">
        {steps.map((step, index) => (
          <div key={step.id} className="flex items-center">
            <div className={`flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium
              ${currentStep > step.id ? 'bg-blue-600 text-white' :
                currentStep === step.id ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-600'}`}>
              {currentStep > step.id ? <Check size={16} /> : step.id}
            </div>
            <span className={`ml-2 text-sm font-medium ${currentStep >= step.id ? 'text-blue-600' : 'text-gray-400'}`}>
              {step.title}
            </span>
            {index < steps.length - 1 && (
              <div className={`w-12 h-0.5 mx-4 ${currentStep > step.id ? 'bg-blue-600' : 'bg-gray-200'}`} />
            )}
          </div>
        ))}
      </div>
    </div>
  );

  // Input & Select Field Reusable Components
  type InputFieldProps = {
    label: string;
    placeholder?: string;
    value: string;
    onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
    type?: string;
    required?: boolean;
  };

  const InputField: React.FC<InputFieldProps> = ({
    label,
    placeholder = '',
    value,
    onChange,
    type = 'text',
    required = false,
  }) => (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange} 
        required={required}
        className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
      />
    </div>
  );

  type SelectFieldProps = {
    label: string;
    value: string;
    onChange: (e: React.ChangeEvent<HTMLSelectElement>) => void;
    options: string[];
    required?: boolean; 
  };

  const SelectField: React.FC<SelectFieldProps> = ({
    label,
    value,
    onChange,
    options,
    required = false,
  }) => (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <select
        value={value}
        onChange={onChange}
        required={required}
        className="block w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-primary"
      >
        <option value="">Select {label}</option>
        {options.map(option => (
          <option key={option} value={option}>{option}</option>
        ))}
      </select>
    </div>
  );

  // Step Components
  const Step1 = () => (
    <>
      <h2 className="text-xl font-semibold text-gray-900 mb-4">Policy Information</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField label="Policy Number" value={formData.policyNo} onChange={(e:any) => handleChange(e, 'policyNo')} required />
        <SelectField label="Policy Status" value={formData.policyStatus} onChange={(e:any) => handleChange(e, 'policyStatus')} options={['Active', 'Pending', 'Cancelled', 'Suspended']} required />
        <SelectField label="Policy Type" value={formData.policyType} onChange={(e:any) => handleChange(e, 'policyType')} options={['Auto', 'Home', 'Life', 'Business']} required />
        <div></div>
        <InputField label="Effective Date" type="date" value={formData.policyEffectiveDate} onChange={(e:any) => handleChange(e, 'policyEffectiveDate')} required />
        <InputField label="Expiration Date" type="date" value={formData.policyExpirationDate} onChange={(e:any) => handleChange(e, 'policyExpirationDate')} required />
      </div>
    </>
  );

  const Step2 = () => (
    <>
      <h2 className="text-xl font-semibold text-gray-900 mb-4">Policy Holder</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField label="First Name" value={formData.policyHolder.firstName} onChange={(e:any) => handleChange(e, 'policyHolder.firstName')} required />
        <InputField label="Last Name" value={formData.policyHolder.lastName} onChange={(e:any) => handleChange(e, 'policyHolder.lastName')} required />
        <InputField label="Street" value={formData.policyHolder.address.street} onChange={(e:any) => handleChange(e, 'policyHolder.address.street')} required />
        <InputField label="City" value={formData.policyHolder.address.city} onChange={(e:any) => handleChange(e, 'policyHolder.address.city')} required />
        <InputField label="State" value={formData.policyHolder.address.state} onChange={(e:any) => handleChange(e, 'policyHolder.address.state')} required />
        <InputField label="ZIP Code" value={formData.policyHolder.address.zip} onChange={(e:any) => handleChange(e, 'policyHolder.address.zip')} required />
      </div>
    </>
  );

  const Step3 = () => (
    <>
      <h2 className="text-xl font-semibold text-gray-900 mb-4">Driver Details</h2>
      {formData.drivers.map((driver, idx) => (
        <div key={idx} className="border rounded-lg p-4 mb-4 relative">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <InputField label="First Name" value={driver.firstName} onChange={(e:any) => handleChange(e, `drivers.${idx}.firstName`)} required />
            <InputField label="Last Name" value={driver.lastName} onChange={(e:any) => handleChange(e, `drivers.${idx}.lastName`)} required />
            <InputField label="Age" value={driver.age} onChange={(e:any) => handleChange(e, `drivers.${idx}.age`)} required />
            <SelectField label="Gender" value={driver.gender} onChange={(e:any) => handleChange(e, `drivers.${idx}.gender`)} options={['Male', 'Female', 'Other']} required />
            <SelectField label="Marital Status" value={driver.maritalStatus} onChange={(e:any) => handleChange(e, `drivers.${idx}.maritalStatus`)} options={['Single', 'Married']} required />
            <InputField label="License Number" value={driver.licenseNumber} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseNumber`)} required />
            <InputField label="License State" value={driver.licenseState} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseState`)} required />
            <SelectField label="License Status" value={driver.licenseStatus} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseStatus`)} options={['Valid', 'Suspended', 'Expired']} required />
            <InputField label="License Effective Date" type="date" value={driver.licenseEffectiveDate} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseEffectiveDate`)} required />
            <InputField label="License Expiration Date" type="date" value={driver.licenseExpirationDate} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseExpirationDate`)} required />
            <InputField label="License Class" value={driver.licenseClass} onChange={(e:any) => handleChange(e, `drivers.${idx}.licenseClass`)} required />
          </div>
          {formData.drivers.length > 1 && (
            <button type="button" onClick={() => removeDriver(idx)} className="absolute top-2 right-2 text-red-500 hover:text-red-700"><Trash2 size={18} /></button>
          )}
        </div>
      ))}
      <button type="button" onClick={addDriver} className="flex items-center text-blue-600 hover:text-blue-800 font-medium mb-4"><PlusCircle className="mr-1" size={18} /> Add Driver</button>
    </>
  );

  const Step4 = () => (
    <>
      <h2 className="text-xl font-semibold text-gray-900 mb-4">Vehicle & Coverage</h2>
      {formData.vehicles.map((vehicle, vIdx) => (
        <div key={vIdx} className="border rounded-lg p-4 mb-4 relative">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <InputField label="Year" value={vehicle.year} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.year`)} required />
            <InputField label="Make" value={vehicle.make} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.make`)} required />
            <InputField label="Model" value={vehicle.model} onChange={(e: any) => handleChange(e, `vehicles.${vIdx}.model`)} required />
            <InputField label="VIN" value={vehicle.vin} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.vin`)} required />
            <InputField label="Usage" value={vehicle.usage} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.usage`)} required />
            <InputField label="Primary Use" value={vehicle.primaryUse} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.primaryUse`)} required />
            <InputField label="Annual Mileage" value={vehicle.annualMileage} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.annualMileage`)} required />
            <InputField label="Ownership" value={vehicle.ownership} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.ownership`)} required />
            <InputField label="Street" value={vehicle.garagingAddress.street} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.garagingAddress.street`)} required />
            <InputField label="City" value={vehicle.garagingAddress.city} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.garagingAddress.city`)} required />
            <InputField label="State" value={vehicle.garagingAddress.state} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.garagingAddress.state`)} required />
            <InputField label="ZIP Code" value={vehicle.garagingAddress.zip} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.garagingAddress.zip`)} required />
          </div>
          <h3 className="text-lg font-medium text-gray-800 mt-6 mb-4">Coverages</h3>
          {vehicle.coverages.map((coverage, cIdx) => (
            <div key={cIdx} className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4 relative">
              <SelectField label="Type" value={coverage.type} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.coverages.${cIdx}.type`)} options={['Liability', 'Collision', 'Comprehensive']} required />
              <InputField label="Limit" value={coverage.limit} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.coverages.${cIdx}.limit`)} required />
              <InputField label="Deductible" value={coverage.deductible} onChange={(e:any) => handleChange(e, `vehicles.${vIdx}.coverages.${cIdx}.deductible`)} required />
              {vehicle.coverages.length > 1 && (
                <button type="button" onClick={() => removeCoverage(vIdx, cIdx)} className="absolute top-2 right-2 text-red-500 hover:text-red-700"><Trash2 size={18} /></button>
              )}
            </div>
          ))}
          <button type="button" onClick={() => addCoverage(vIdx)} className="flex items-center text-blue-600 hover:text-blue-800 font-medium mb-4"><PlusCircle className="mr-1" size={18} /> Add Coverage</button>
          {formData.vehicles.length > 1 && (
            <button type="button" onClick={() => removeVehicle(vIdx)} className="absolute top-2 right-2 text-red-500 hover:text-red-700"><Trash2 size={18} /></button>
          )}
        </div>
      ))}
      <button type="button" onClick={addVehicle} className="flex items-center text-blue-600 hover:text-blue-800 font-medium mb-4"><PlusCircle className="mr-1" size={18} /> Add Vehicle</button>
    </>
  );

  const renderStep = () => {
    switch (currentStep) {
      case 1: return <Step1 />;
      case 2: return <Step2 />;
      case 3: return <Step3 />;
      case 4: return <Step4 />;
      default: return null;
    }
  };

  return (
    <div className=" bg-white">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
        <StepHeader />
        <div>{renderStep()}</div>

        <div className="flex justify-between mt-8">
          <button
            type="button"
            onClick={prevStep}
            disabled={currentStep === 1}
            className={`flex items-center px-4 py-2 rounded-md font-medium ${
              currentStep === 1
                ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
            }`}
          >
            <ChevronLeft size={16} className="mr-1" />
            Previous
          </button>

          {currentStep < steps.length ? (
            <button
              type="button"
              onClick={nextStep}
              className="flex items-center px-6 py-2 rounded-md font-medium bg-blue-600 text-white hover:bg-blue-700"
            >
              Save & Continue
              <ChevronRight size={16} className="ml-1" />
            </button>
          ) : (
            <button
              type="button"
              onClick={handleSubmit}
              className="px-6 py-2 bg-green-600 text-white rounded-md font-medium hover:bg-green-700"
            >
              Submit Policy
            </button>
          )}
        </div>

        <p className="text-xs text-gray-500 mt-6">*All fields are required.</p>
      </div>
    </div>
  );
}
