import React from 'react';
import { Home, FileText, PlusCircle, Pencil, BarChart2 } from 'lucide-react';
import { NavLink } from 'react-router-dom';

const sidebarItems = [
  { name: 'Home', icon: Home, route: '/home' },
  { name: 'Policies', icon: FileText, route: '/policies' },
  { name: 'Add Policy', icon: PlusCircle, route: '/add-policy' },
  { name: 'Edit Policy', icon: Pencil, route: '/edit-policy' },
  { name: 'Reports', icon: BarChart2, route: '/reports' },
];

const LeftSidebar: React.FC = () => {
  return (
    <div className="w-16 md:w-64 bg-white border-r border-gray-200 fixed h-full">
      <div className="p-4">
        <h1 className="text-xl font-bold text-gray-800 hidden md:block">Car4Sure</h1>
        <div className="mt-8 space-y-4">
          {sidebarItems.map((item, idx) => (
            <React.Fragment key={item.name}>
              {item.name === 'Edit Policy' && <hr />}
              <NavLink
                to={item.route}
                className={({ isActive }) =>
                  `flex items-center space-x-3 p-2 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors duration-150 ` +
                  (isActive ? 'bg-primary text-white hover:bg-primary' : '')
                }
                end
              >
                <item.icon className={`w-6 h-6 ${window.location.pathname === item.route ? 'text-white' : 'text-gray-600'}`} />
                <span className="hidden md:block">{item.name}</span>
              </NavLink>
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LeftSidebar;
