import React from 'react';
import LeftSidebar from './LeftSidebar';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  return (
    <div className="flex h-screen bg-gray-100">
      {/* Left Sidebar */}
      <LeftSidebar />

      {/* Main Content */}
      <div className="flex-1 ml-16 md:ml-64">
        <main className="max-w-2xl mx-auto p-4">
          {children}
        </main>
      </div>

      {/* Right Panel */}
      <div className="hidden lg:block w-80 bg-white border-l border-gray-200 fixed right-0 h-full">
        <div className="p-4">
          <h3 className="text-lg font-semibold mb-4">Bank Stats</h3>
          <div className="space-y-4">
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium">Account Balance</h4>
              <p className="text-2xl font-bold text-green-600">$25,000</p>
            </div>
            <div className="bg-gray-50 p-4 rounded-lg">
              <h4 className="font-medium">Recent Transactions</h4>
              <ul className="mt-2 space-y-2">
                <li className="text-sm text-gray-600">Payment to John Doe</li>
                <li className="text-sm text-gray-600">Salary Deposit</li>
                <li className="text-sm text-gray-600">Utility Bill Payment</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Layout;
