import React, { useState } from 'react';
import { ChevronLeft, ChevronRight, Check } from 'lucide-react';

const initialState = {
  policyNo: '',
  policyStatus: '',
  policyType: '',
  policyEffectiveDate: '',
  policyExpirationDate: '',
  policyHolder: {
    firstName: '',
    lastName: '',
    address: {
      street: '',
      city: '',
      state: '',
      zip: ''
    }
  },
  drivers: [{
    firstName: '',
    lastName: '',
    age: '',
    gender: '',
    maritalStatus: '',
    licenseNumber: '',
    licenseState: '',
    licenseStatus: '',
    licenseEffectiveDate: '',
    licenseExpirationDate: '',
    licenseClass: ''
  }],
  vehicles: [{
    year: '',
    make: '',
    model: '',
    vin: '',
    usage: '',
    primaryUse: '',
    annualMileage: '',
    ownership: '',
    garagingAddress: {
      street: '',
      city: '',
      state: '',
      zip: ''
    },
    coverages: [
      { type: 'Liability', limit: '', deductible: '' },
      { type: 'Collision', limit: '', deductible: '' },
      { type: 'Comprehensive', limit: '', deductible: '' }
    ]
  }]
};

const steps = [
  { id: 1, title: 'Policy Info', description: 'Basic policy information' },
  { id: 2, title: 'Policy Holder', description: 'Personal information' },
  { id: 3, title: 'Driver Details', description: 'License information' },
  { id: 4, title: 'Vehicle & Coverage', description: 'Vehicle and coverage details' }
];

export default function PolicyForm() {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState(initialState);

  const handleChange = (e, path) => {
    const value = e.target.value;
    setFormData(prev => {
      const updated = { ...prev };
      const keys = path.split('.');
      let ref = updated;
      while (keys.length > 1) {
        ref = ref[keys.shift()];
      }
      ref[keys[0]] = value;
      return updated;
    });
  };

  const handleSubmit = () => {
    console.log(formData);
    // Send formData to backend API
  };

  const nextStep = () => {
    if (currentStep < steps.length) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const StepHeader = () => (
    <div className="flex items-center justify-center mb-8">
      <div className="flex items-center space-x-4">
        {steps.map((step, index) => (
          <div key={step.id} className="flex items-center">
            <div className={`flex items-center justify-center w-8 h-8 rounded-full text-sm font-medium
              ${currentStep > step.id ? 'bg-blue-600 text-white' : 
                currentStep === step.id ? 'bg-blue-600 text-white' : 'bg-gray-200 text-gray-600'}`}>
              {currentStep > step.id ? <Check size={16} /> : step.id}
            </div>
            <span className={`ml-2 text-sm font-medium ${currentStep >= step.id ? 'text-blue-600' : 'text-gray-400'}`}>
              {step.title}
            </span>
            {index < steps.length - 1 && (
              <div className={`w-12 h-0.5 mx-4 ${currentStep > step.id ? 'bg-blue-600' : 'bg-gray-200'}`} />
            )}
          </div>
        ))}
      </div>
    </div>
  );

  const InputField = ({ label, placeholder, value, onChange, type = 'text', required = false }) => (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        required={required}
      />
    </div>
  );

  const SelectField = ({ label, value, onChange, options, required = false }) => (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-2">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <select
        value={value}
        onChange={onChange}
        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        required={required}
      >
        <option value="">Select {label}</option>
        {options.map(option => (
          <option key={option} value={option}>{option}</option>
        ))}
      </select>
    </div>
  );

  const Step1 = () => (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6">Policy Information</h2>
      <p className="text-sm text-gray-600 mb-6">Provide your policy details</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField
          label="Policy Number"
          placeholder="Enter policy number"
          value={formData.policyNo}
          onChange={e => handleChange(e, 'policyNo')}
          required
        />
        <SelectField
          label="Policy Status"
          value={formData.policyStatus}
          onChange={e => handleChange(e, 'policyStatus')}
          options={['Active', 'Pending', 'Cancelled', 'Suspended']}
          required
        />
        <SelectField
          label="Policy Type"
          value={formData.policyType}
          onChange={e => handleChange(e, 'policyType')}
          options={['Auto', 'Home', 'Life', 'Business']}
          required
        />
        <div></div>
        <InputField
          label="Effective Date"
          type="date"
          value={formData.policyEffectiveDate}
          onChange={e => handleChange(e, 'policyEffectiveDate')}
          required
        />
        <InputField
          label="Expiration Date"
          type="date"
          value={formData.policyExpirationDate}
          onChange={e => handleChange(e, 'policyExpirationDate')}
          required
        />
      </div>
    </div>
  );

  const Step2 = () => (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6">Policy Holder</h2>
      <p className="text-sm text-gray-600 mb-6">Provide your personal information</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField
          label="First Name"
          placeholder="Enter first name"
          value={formData.policyHolder.firstName}
          onChange={e => handleChange(e, 'policyHolder.firstName')}
          required
        />
        <InputField
          label="Last Name"
          placeholder="Enter last name"
          value={formData.policyHolder.lastName}
          onChange={e => handleChange(e, 'policyHolder.lastName')}
          required
        />
      </div>
      
      <h3 className="text-lg font-medium text-gray-800 mt-6 mb-4">Address</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="md:col-span-2">
          <InputField
            label="Street Address"
            placeholder="Enter street address"
            value={formData.policyHolder.address.street}
            onChange={e => handleChange(e, 'policyHolder.address.street')}
            required
          />
        </div>
        <InputField
          label="City"
          placeholder="Enter city"
          value={formData.policyHolder.address.city}
          onChange={e => handleChange(e, 'policyHolder.address.city')}
          required
        />
        <InputField
          label="State"
          placeholder="Enter state"
          value={formData.policyHolder.address.state}
          onChange={e => handleChange(e, 'policyHolder.address.state')}
          required
        />
        <InputField
          label="ZIP Code"
          placeholder="Enter ZIP code"
          value={formData.policyHolder.address.zip}
          onChange={e => handleChange(e, 'policyHolder.address.zip')}
          required
        />
      </div>
    </div>
  );

  const Step3 = () => (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6">Driver Details</h2>
      <p className="text-sm text-gray-600 mb-6">Provide driver information</p>
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField
          label="First Name"
          placeholder="Enter first name"
          value={formData.drivers[0].firstName}
          onChange={e => handleChange(e, 'drivers.0.firstName')}
          required
        />
        <InputField
          label="Last Name"
          placeholder="Enter last name"
          value={formData.drivers[0].lastName}
          onChange={e => handleChange(e, 'drivers.0.lastName')}
          required
        />
        <InputField
          label="Age"
          type="number"
          placeholder="Enter age"
          value={formData.drivers[0].age}
          onChange={e => handleChange(e, 'drivers.0.age')}
          required
        />
        <SelectField
          label="Gender"
          value={formData.drivers[0].gender}
          onChange={e => handleChange(e, 'drivers.0.gender')}
          options={['Male', 'Female', 'Other']}
          required
        />
        <SelectField
          label="Marital Status"
          value={formData.drivers[0].maritalStatus}
          onChange={e => handleChange(e, 'drivers.0.maritalStatus')}
          options={['Single', 'Married', 'Divorced', 'Widowed']}
          required
        />
        <InputField
          label="License Number"
          placeholder="Enter license number"
          value={formData.drivers[0].licenseNumber}
          onChange={e => handleChange(e, 'drivers.0.licenseNumber')}
          required
        />
        <InputField
          label="License State"
          placeholder="Enter license state"
          value={formData.drivers[0].licenseState}
          onChange={e => handleChange(e, 'drivers.0.licenseState')}
          required
        />
        <SelectField
          label="License Status"
          value={formData.drivers[0].licenseStatus}
          onChange={e => handleChange(e, 'drivers.0.licenseStatus')}
          options={['Valid', 'Suspended', 'Revoked', 'Expired']}
          required
        />
        <InputField
          label="License Effective Date"
          type="date"
          value={formData.drivers[0].licenseEffectiveDate}
          onChange={e => handleChange(e, 'drivers.0.licenseEffectiveDate')}
          required
        />
        <InputField
          label="License Expiration Date"
          type="date"
          value={formData.drivers[0].licenseExpirationDate}
          onChange={e => handleChange(e, 'drivers.0.licenseExpirationDate')}
          required
        />
        <InputField
          label="License Class"
          placeholder="Enter license class"
          value={formData.drivers[0].licenseClass}
          onChange={e => handleChange(e, 'drivers.0.licenseClass')}
          required
        />
      </div>
    </div>
  );

  const Step4 = () => (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold text-gray-900 mb-6">Vehicle & Coverage</h2>
      <p className="text-sm text-gray-600 mb-6">Provide vehicle and coverage details</p>
      
      <h3 className="text-lg font-medium text-gray-800 mb-4">Vehicle Information</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <InputField
          label="Year"
          type="number"
          placeholder="Enter year"
          value={formData.vehicles[0].year}
          onChange={e => handleChange(e, 'vehicles.0.year')}
          required
        />
        <InputField
          label="Make"
          placeholder="Enter make"
          value={formData.vehicles[0].make}
          onChange={e => handleChange(e, 'vehicles.0.make')}
          required
        />
        <InputField
          label="Model"
          placeholder="Enter model"
          value={formData.vehicles[0].model}
          onChange={e => handleChange(e, 'vehicles.0.model')}
          required
        />
        <InputField
          label="VIN"
          placeholder="Enter VIN"
          value={formData.vehicles[0].vin}
          onChange={e => handleChange(e, 'vehicles.0.vin')}
          required
        />
        <InputField
          label="Usage"
          placeholder="Enter usage"
          value={formData.vehicles[0].usage}
          onChange={e => handleChange(e, 'vehicles.0.usage')}
          required
        />
        <InputField
          label="Primary Use"
          placeholder="Enter primary use"
          value={formData.vehicles[0].primaryUse}
          onChange={e => handleChange(e, 'vehicles.0.primaryUse')}
          required
        />
        <InputField
          label="Annual Mileage"
          type="number"
          placeholder="Enter annual mileage"
          value={formData.vehicles[0].annualMileage}
          onChange={e => handleChange(e, 'vehicles.0.annualMileage')}
          required
        />
        <InputField
          label="Ownership"
          placeholder="Enter ownership"
          value={formData.vehicles[0].ownership}
          onChange={e => handleChange(e, 'vehicles.0.ownership')}
          required
        />
      </div>

      <h3 className="text-lg font-medium text-gray-800 mt-6 mb-4">Garaging Address</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="md:col-span-2">
          <InputField
            label="Street Address"
            placeholder="Enter street address"
            value={formData.vehicles[0].garagingAddress.street}
            onChange={e => handleChange(e, 'vehicles.0.garagingAddress.street')}
            required
          />
        </div>
        <InputField
          label="City"
          placeholder="Enter city"
          value={formData.vehicles[0].garagingAddress.city}
          onChange={e => handleChange(e, 'vehicles.0.garagingAddress.city')}
          required
        />
        <InputField
          label="State"
          placeholder="Enter state"
          value={formData.vehicles[0].garagingAddress.state}
          onChange={e => handleChange(e, 'vehicles.0.garagingAddress.state')}
          required
        />
        <InputField
          label="ZIP Code"
          placeholder="Enter ZIP code"
          value={formData.vehicles[0].garagingAddress.zip}
          onChange={e => handleChange(e, 'vehicles.0.garagingAddress.zip')}
          required
        />
      </div>

      <h3 className="text-lg font-medium text-gray-800 mt-6 mb-4">Coverage Details</h3>
      <div className="space-y-4">
        {formData.vehicles[0].coverages.map((coverage, i) => (
          <div key={i} className="p-4 border border-gray-200 rounded-lg">
            <h4 className="font-medium text-gray-800 mb-3">{coverage.type}</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <InputField
                label="Limit"
                type="number"
                placeholder="Enter limit"
                value={coverage.limit}
                onChange={e => handleChange(e, `vehicles.0.coverages.${i}.limit`)}
                required
              />
              <InputField
                label="Deductible"
                type="number"
                placeholder="Enter deductible"
                value={coverage.deductible}
                onChange={e => handleChange(e, `vehicles.0.coverages.${i}.deductible`)}
                required
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  const renderStep = () => {
    switch (currentStep) {
      case 1: return <Step1 />;
      case 2: return <Step2 />;
      case 3: return <Step3 />;
      case 4: return <Step4 />;
      default: return <Step1 />;
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white">
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
        <StepHeader />
        
        <div>
          {renderStep()}
          
          <div className="flex justify-between mt-8">
            <button
              type="button"
              onClick={prevStep}
              disabled={currentStep === 1}
              className={`flex items-center px-4 py-2 rounded-md font-medium ${
                currentStep === 1
                  ? 'bg-gray-100 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <ChevronLeft size={16} className="mr-1" />
              Previous
            </button>
            
            {currentStep < steps.length ? (
              <button
                type="button"
                onClick={nextStep}
                className="flex items-center px-6 py-2 bg-blue-600 text-white rounded-md font-medium hover:bg-blue-700"
              >
                Save & Continue
                <ChevronRight size={16} className="ml-1" />
              </button>
            ) : (
              <button
                type="button"
                onClick={handleSubmit}
                className="px-6 py-2 bg-blue-600 text-white rounded-md font-medium hover:bg-blue-700"
              >
                Submit Policy
              </button>
            )}
          </div>
        </div>
        
        <p className="text-xs text-gray-500 mt-6">
          *This field is mandatory
        </p>
      </div>
    </div>
  );
}
