import React, { useState } from 'react';
import { Trash2 } from 'lucide-react'; // Importing Trash2 icon from lucide-react
import { Heading } from '../components/Heading'; // Assuming this path is correct for your Heading component

// Define the Policy interface for type safety
interface Policy {
  id: string;
  policyNumber: string;
  policyType: string;
  status: string;
  effectiveDate: string;
  expirationDate: string;
  holderName: string;
  vehicle: string;
}

// Define the Policies functional component
const Policies: React.FC = () => {
  // State for managing the search query input
  const [searchQuery, setSearchQuery] = useState<string>('');
  // State for managing the visibility of the "Actions" dropdown
  const [actionsDropdown, setActionsDropdown] = useState<boolean>(false);
  // State for controlling the visibility of individual item action dropdowns
  const [itemDropdowns, setItemDropdowns] = useState<{ [key: string]: boolean }>({});

  // Sample policy data
  const policies: Policy[] = [
    { id: 'POL001', policyNumber: "P-1001", policyType: "Auto", status: "Active", effectiveDate: "2023-01-15", expirationDate: "2024-01-15", holderName: "Alice Smith", vehicle: "Toyota Camry" },
    { id: 'POL002', policyNumber: "P-1002", policyType: "Home", status: "Active", effectiveDate: "2022-03-01", expirationDate: "2025-03-01", holderName: "Bob Johnson", vehicle: "N/A" },
    { id: 'POL003', policyNumber: "P-1003", policyType: "Life", status: "Pending", effectiveDate: "2023-06-20", expirationDate: "2053-06-20", holderName: "Charlie Brown", vehicle: "N/A" },
    { id: 'POL004', policyNumber: "P-1004", policyType: "Auto", status: "Expired", effectiveDate: "2021-11-10", expirationDate: "2022-11-10", holderName: "Diana Prince", vehicle: "Honda Civic" },
    { id: 'POL005', policyNumber: "P-1005", policyType: "Health", status: "Active", effectiveDate: "2023-09-01", expirationDate: "2024-09-01", holderName: "Clark Kent", vehicle: "N/A" },
    { id: 'POL006', policyNumber: "P-1006", policyType: "Auto", status: "Active", effectiveDate: "2024-02-01", expirationDate: "2025-02-01", holderName: "Bruce Wayne", vehicle: "Batmobile" },
    { id: 'POL007', policyNumber: "P-1007", policyType: "Home", status: "Cancelled", effectiveDate: "2020-05-01", expirationDate: "2023-05-01", holderName: "Selina Kyle", vehicle: "N/A" },
  ];

  // Handler for search input changes
  const handleSearch = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
  };

  // Toggles the visibility of the "Actions" dropdown
  const toggleActionsDropdown = () => {
    setActionsDropdown(prev => !prev);
    // No filter dropdown to close here as it's removed for now
  };

  // Toggles the visibility of an individual item's dropdown
  const toggleItemDropdown = (id: string) => {
    setItemDropdowns(prev => ({ ...prev, [id]: !prev[id] }));
  };

  // Filter policies based on search query
  const filteredPolicies = policies.filter(policy =>
    policy.policyNumber.toLowerCase().includes(searchQuery.toLowerCase()) ||
    policy.policyType.toLowerCase().includes(searchQuery.toLowerCase()) ||
    policy.status.toLowerCase().includes(searchQuery.toLowerCase()) ||
    policy.holderName.toLowerCase().includes(searchQuery.toLowerCase()) ||
    policy.vehicle.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <>
      <Heading name="Policies" description="Browse and manage all your insurance policies in one place." />

      <section className="dark:bg-gray-900 min-h-screen font-sans">
        <div className="">
          <div className="bg-white dark:bg-gray-800 relative shadow-md sm:rounded-lg overflow-hidden">
            {/* Top bar with search, add policy, and actions buttons */}
            <div className="flex flex-col md:flex-row items-center justify-between space-y-3 md:space-y-0 md:space-x-4 p-4">
              {/* Search input */}
              <div className="w-full md:w-1/2">
                <div className="relative w-full">
                  <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
                    <svg className="w-5 h-5 text-gray-500 dark:text-gray-400" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                      <path fillRule="evenodd" d="M8 4a4 4 0 100 8 4 4 0 000-8zM2 8a6 6 0 1110.89 3.476l4.817 4.817a1 1 0 01-1.414 1.414l-4.816-4.816A6 6 0 012 8z" clipRule="evenodd" />
                    </svg>
                  </div>
                  <input
                    type="text"
                    id="simple-search"
                    className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full pl-10 p-2 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                    placeholder="Search policies..."
                    value={searchQuery}
                    onChange={handleSearch}
                  />
                </div>
              </div>
              {/* Action buttons: Add Policy, Actions Dropdown */}
              <div className="w-full md:w-auto flex flex-col md:flex-row space-y-2 md:space-y-0 items-stretch md:items-center justify-end md:space-x-3 flex-shrink-0">
                {/* Add Policy Button */}
                <button
                  type="button"
                  className="flex items-center justify-center text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 focus:outline-none dark:focus:ring-blue-800"
                >
                  <svg className="h-3.5 w-3.5 mr-2" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                    <path clipRule="evenodd" fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" />
                  </svg>
                  Add Policy
                </button>
                {/* Actions Dropdown */}
                <div className="flex items-center space-x-3 w-full md:w-auto relative">
                  <button
                    id="actionsDropdownButton"
                    onClick={toggleActionsDropdown}
                    className="w-full md:w-auto flex items-center justify-center py-2 px-4 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700"
                    type="button"
                  >
                    <svg className="-ml-1 mr-1.5 w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                      <path clipRule="evenodd" fillRule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" />
                    </svg>
                    Actions
                  </button>
                  {actionsDropdown && (
                    <div className="absolute top-full mt-2 z-10 w-44 bg-white rounded divide-y divide-gray-100 shadow dark:bg-gray-700 dark:divide-gray-600 right-0 md:left-0">
                      <ul className="py-1 text-sm text-gray-700 dark:text-gray-200">
                        <li>
                          <a href="#" className="block py-2 px-4 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Mass Edit Policies</a>
                        </li>
                      </ul>
                      <div className="py-1">
                        <a href="#" className="block py-2 px-4 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white">Delete all Policies</a>
                      </div>
                    </div>
                  )}
                  {/* The filter dropdown button and content are removed as per the new requirements.
                      If you need policy-specific filters (e.g., by Policy Type or Status), please let me know!
                  */}
                </div>
              </div>
            </div>
            {/* Policy Table */}
            <div className="overflow-x-auto">
              <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
                <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                  <tr>
                    <th scope="col" className="px-4 py-3">Policy Number</th>
                    <th scope="col" className="px-4 py-3">Policy Type</th>
                    <th scope="col" className="px-4 py-3">Status</th>
                    <th scope="col" className="px-4 py-3">Effective Date</th>
                    <th scope="col" className="px-4 py-3">Expiration Date</th>
                    <th scope="col" className="px-4 py-3">Holder Name</th>
                    <th scope="col" className="px-4 py-3">Vehicle</th>
                    <th scope="col" className="px-4 py-3">
                      <span className="sr-only">Actions</span>
                    </th>
                  </tr>
                </thead>
                <tbody>
                  {filteredPolicies.map(policy => (
                    <tr key={policy.id} className="border-b dark:border-gray-700">
                      <th scope="row" className="px-4 py-3 font-medium text-gray-900 whitespace-nowrap dark:text-white">{policy.policyNumber}</th>
                      <td className="px-4 py-3">{policy.policyType}</td>
                      <td className="px-4 py-3">{policy.status}</td>
                      <td className="px-4 py-3">{policy.effectiveDate}</td>
                      <td className="px-4 py-3">{policy.expirationDate}</td>
                      <td className="px-4 py-3">{policy.holderName}</td>
                      <td className="px-4 py-3">{policy.vehicle}</td>
                      <td className="px-4 py-3 flex items-center justify-end relative">
                        <button
                          onClick={() => toggleItemDropdown(policy.id)}
                          className="inline-flex items-center p-0.5 text-sm font-medium text-center text-gray-500 hover:text-gray-800 rounded-lg focus:outline-none dark:text-gray-400 dark:hover:text-gray-100"
                          type="button"
                        >
                          <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                            <path d="M6 10a2 2 0 11-4 0 2 2 0 014 0zM12 10a2 2 0 11-4 0 2 2 0 014 0zM16 12a2 2 0 100-4 2 2 0 000 4z" />
                          </svg>
                        </button>
                        {itemDropdowns[policy.id] && (
                          <div className="absolute top-full mt-2 z-10 w-44 bg-white rounded divide-y divide-gray-100 shadow dark:bg-gray-700 dark:divide-gray-600 right-0">
                            <ul className="py-1 text-sm text-gray-700 dark:text-gray-200">
                              <li>
                                <a href="#" className="block py-2 px-4 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">View Details</a>
                              </li>
                              <li>
                                <a href="#" className="block py-2 px-4 hover:bg-gray-100 dark:hover:bg-gray-600 dark:hover:text-white">Edit Policy</a>
                              </li>
                            </ul>
                            <div className="py-1">
                              <a href="#" className="block py-2 px-4 text-sm text-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600 dark:text-gray-200 dark:hover:text-white flex items-center">
                                <Trash2 className="w-4 h-4 mr-2" /> Delete Policy
                              </a>
                            </div>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            {/* Pagination */}
            <nav className="flex flex-col md:flex-row justify-between items-start md:items-center space-y-3 md:space-y-0 p-4" aria-label="Table navigation">
              <span className="text-sm font-normal text-gray-500 dark:text-gray-400">
                Showing
                <span className="font-semibold text-gray-900 dark:text-white">1-{filteredPolicies.length}</span>
                of
                <span className="font-semibold text-gray-900 dark:text-white">{policies.length}</span>
              </span>
              <ul className="inline-flex items-stretch -space-x-px">
                <li>
                  <a href="#" className="flex items-center justify-center h-full py-1.5 px-3 ml-0 text-gray-500 bg-white rounded-l-lg border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
                    <span className="sr-only">Previous</span>
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                      <path fillRule="evenodd" d="M12.707 5.293a1 1 0 010 1.414L9.414 10l3.293 3.293a1 1 0 01-1.414 1.414l-4-4a1 1 0 010-1.414l4-4a1 1 0 011.414 0z" clipRule="evenodd" />
                    </svg>
                  </a>
                </li>
                {/* Simplified pagination for demonstration */}
                <li>
                  <a href="#" aria-current="page" className="flex items-center justify-center text-sm z-10 py-2 px-3 leading-tight text-blue-600 bg-blue-50 border border-blue-300 hover:bg-blue-100 hover:text-blue-700 dark:border-gray-700 dark:bg-gray-700 dark:text-white">1</a>
                </li>
                <li>
                  <a href="#" className="flex items-center justify-center h-full py-1.5 px-3 leading-tight text-gray-500 bg-white rounded-r-lg border border-gray-300 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white">
                    <span className="sr-only">Next</span>
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                      <path fillRule="evenodd" d="M7.293 14.707a1 1 0 010-1.414L10.586 10 7.293 6.707a1 1 0 011.414-1.414l4 4a1 1 0 010 1.414l-4 4a1 1 0 01-1.414 0z" clipRule="evenodd" />
                    </svg>
                  </a>
                </li>
              </ul>
            </nav>
          </div>
        </div>
      </section>
    </>
  );
};

export default Policies;
